import React, { Component } from 'react';
import './App.css';

import axios from 'axios';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			API_key: '7c322888d234d1fc7dc225ad7e3e1e03',
			weatherData: null,
			value: null
		};
		this.handleChange = this.handleChange.bind(this);
	}

	getWeatherData = () => {
		let url = `https://api.openweathermap.org/data/2.5/find?q=${this.state
			.value}&units=metric&lang=hr&appid=7c322888d234d1fc7dc225ad7e3e1e03`;
		axios
			.get(url)
			.then((res) => {
				const weatherData = res.data;
        this.setState({ weatherData: weatherData });
			})
			.catch((e) => {
        this.setState({ weatherData: null})
				console.log(e);
			});
	};

	handleChange = (e) => {
		// e.preventDefault();
		this.setState({ value: e.target.value });
	};

	render() {
		let city;
		let temperature;
		let humidity;
    let clouds;
    let icon;
		if (this.state.weatherData) {
      console.log(this.state.weatherData)
      if( this.state.weatherData.count > 0){
        city = <p>Grad: {this.state.weatherData.list[0].name}</p>;
        temperature = <p>Temperatura: {this.state.weatherData.list[0].main.temp}deg</p>;
        humidity = <p>Vlaznost vazduha: {this.state.weatherData.list[0].main.humidity}%</p>;
        clouds = <p>Oblaci: {this.state.weatherData.list[0].weather[0].description}</p>;
        icon = <p>Ikonica: {this.state.weatherData.list[0].weather[0].icon}</p>;
      }
		}
		return (
			<React.Fragment>
				<input type="text" placeholder="grad" onChange={this.handleChange} />
				<button onClick={this.getWeatherData}>Get Weather Data</button>
				{city}
				{temperature}
				{humidity}
        {clouds}
        {icon}
			</React.Fragment>
		);
	}
}

export default App;
