import React, { Component } from 'react';
import './App.css';

import axios from 'axios';

class App extends Component {
	state = {
		API_key: '7c322888d234d1fc7dc225ad7e3e1e03',
    weatherData: null,
    value: null
  };

  constructor(props) {
    super(props);
    this.state = {
      API_key: '7c322888d234d1fc7dc225ad7e3e1e03',
      weatherData: null,
      value: null
    }
    this.handleChange = this.handleChange.bind(this);
  }

	getWeatherData = () => {
		console.log('getting data bitch');

		let url =
      `https://api.openweathermap.org/data/2.5/find?q=${this.state.value}&units=metric&appid=7c322888d234d1fc7dc225ad7e3e1e03`;

		axios
			.get(url)
			.then((res) => {
				const weatherData = res.data;
				this.setState({ weatherData: weatherData });
			})
			.catch((e) => {
				console.log(e);
			});
  };

  handleChange(e) {
    // e.preventDefault();
    this.setState({value: e.target.value})
  }

	render() {
		let city;
		let temperature;
		let humidity;
		let clouds;
		if (this.state.weatherData) {
			console.log(this.state.weatherData.list[0]);
			city = <p>City: {this.state.weatherData.list[0].name}</p>;
			temperature = <p>Temperature: {this.state.weatherData.list[0].main.temp}deg</p>;
			humidity = <p>Temperature: {this.state.weatherData.list[0].main.humidity}%</p>;
			clouds = <p>Clouds: {this.state.weatherData.list[0].weather[0].description}</p>;
		}
		return (
			<React.Fragment>
				<form>
					<input type="text" placeholder="London" onChange={this.handleChange} />
					<button onClick={this.getWeatherData}>Get Weather Data</button>
				</form>
				{city}
				{temperature}
				{humidity}
				{clouds}
			</React.Fragment>
		);
	}
}

export default App;
