import React, { Component } from 'react';
import './App.css';

import axios from 'axios';

class App extends Component {
	state = {
		API_key: '7c322888d234d1fc7dc225ad7e3e1e03',
		weatherData: null
	};

	getWeatherData = () => {
		console.log('getting data bitch');

		let url =
			'https://api.openweathermap.org/data/2.5/find?q=Belgrade,rs&units=metric&appid=7c322888d234d1fc7dc225ad7e3e1e03';

		axios
			.get(url)
			.then((res) => {
				const weatherData = res.data;
				this.setState({ weatherData: weatherData });
			})
			.catch((e) => {
				console.log(e);
			});
	};

	render() {
		console.log(this.state.weatherData);
		return (
			<React.Fragment>
				<button onClick={this.getWeatherData}>Get Weather Data</button>
        <p></p>

			</React.Fragment>
		);
	}
}

export default App;
