import React, {Component} from 'react';
import './App.css';

import axios from 'axios';

class App extends Component {

  state = {
    API_key: '7c322888d234d1fc7dc225ad7e3e1e03',
    weatherData: null
  }

  getWeatherData = () => {
    console.log("getting data bitch")

    let url = 'https://samples.openweathermap.org/data/2.5/weather?q=London,uk&appid=7c322888d234d1fc7dc225ad7e3e1e03'

    axios.get(url).then(
      res => {
        const weatherData = res.data;
        this.setState({ weatherData: weatherData})
      }
    )
    .catch(e => {
      console.log(e);
    })

  }

  render() {
    console.log(this.state.weatherData);
    return(
      <button onClick={this.getWeatherData}>
        Get Weather Data
      </button>
    )
  }
}

export default App;
